function updatePinnedClass(){var e=document.querySelector(".page-wrapper");window.innerWidth<1280?e.classList.add("pinned"):e.classList.remove("pinned")}document.addEventListener("DOMContentLoaded",updatePinnedClass),window.addEventListener("resize",updatePinnedClass),document.addEventListener("DOMContentLoaded",()=>{let a=document.querySelectorAll(".tab-link"),n=document.querySelectorAll(".tab-content");a.forEach(t=>{t.addEventListener("click",()=>{var e=t.getAttribute("data-tab");a.forEach(e=>e.classList.remove("active")),n.forEach(e=>e.classList.remove("active")),t.classList.add("active"),document.querySelector(`.tab-content[data-id="${e}"]`).classList.add("active")})});var e=document.querySelector(".toggle-sidebar");let t=document.querySelector(".page-wrapper");e.addEventListener("click",()=>{t.classList.toggle("pinned")})});